"use strict";
(function() {
  var isWindows = navigator.platform.indexOf('Win') > -1 ? true : false;

  if (isWindows) {
    // if we are on windows OS initialize the scrollbar plugin
    if (document.querySelector('#sidenav-scrollbar')) {
      var options = {
        damping: '0.5'
      }
      if (typeof Scrollbar !== 'undefined') {
        Scrollbar.init(document.querySelector('#sidenav-scrollbar'), options);
      }
    }
  }
  
  // Initialization for dropdown toggles
  var dropdownElementList = [].slice.call(document.querySelectorAll('[data-bs-toggle="dropdown"]'))
  if (typeof bootstrap !== 'undefined') {
    var dropdownList = dropdownElementList.map(function (dropdownToggleEl) {
      return new bootstrap.Dropdown(dropdownToggleEl)
    })
  }

  // Toggle the sidenav on mobile
  var iconNavbarSidenav = document.getElementById('iconNavbarSidenav');
  var iconSidenav = document.getElementById('iconSidenav');
  var sidenav = document.getElementById('sidenav-main');
  var body = document.getElementsByTagName('body')[0];
  var className = 'g-sidenav-pinned';
  
  if (iconNavbarSidenav) {
    iconNavbarSidenav.addEventListener("click", toggleSidenav);
  }

  if (iconSidenav) {
    iconSidenav.addEventListener("click", toggleSidenav);
  }

  function toggleSidenav() {
    if (body.classList.contains(className)) {
      body.classList.remove(className);
      setTimeout(function() {
        sidenav.classList.remove('bg-white');
      }, 100);
      sidenav.classList.remove('bg-transparent');
    } else {
      body.classList.add(className);
      sidenav.classList.add('bg-white');
      sidenav.classList.remove('bg-transparent');
    }
  }
})(); 